import React, { useEffect } from 'react';
import { View, Text, FlatList, TouchableOpacity } from 'react-native';

const tempSemesterData = [{
    semester: "Spring 2020",
    courses:[{
        name: "Software Development",
        hours: 4,
        grade: 90.27
    },
    {
        name: "Database Management",
        hours: 3,
        grade: 88.98
    }]},
    {
    semester: "Fall 2019",
    courses:[{
        name: "English",
        hours: 4,
        grade: 95.40
    },
    {
        name: "Math II",
        hours: 3,
        grade: 90.40
    }]},
    {
    semester: "Summer 2019",
    courses:[{
        name: "Public Speaking",
        hours: 3,
        grade: 85.46
    }]},
    {
        semester: "Spring 2019",
        courses:[{
            name: "Software Development",
            hours: 4,
            grade: 90.27
        },
        {
            name: "Database Management",
            hours: 3,
            grade: 88.98
        }]},
        {
        semester: "Fall 2018",
        courses:[{
            name: "English",
            hours: 4,
            grade: 95.40
        },
        {
            name: "Math II",
            hours: 3,
            grade: 90.40
        }]},
        {
        semester: "Summer 2018",
        courses:[{
            name: "Public Speaking",
            hours: 3,
            grade: 85.46
        }]}
]

function AcademicManager(props) {

    useEffect(() => {

    }, []);

    const addSemester = () => {
        props.navigation.navigate("AddSemester");
    }

    const addStudyHours = () => {
        props.navigation.navigate("AddStudyHours");

    }

    return (
        <View style={{display: 'flex', flex: 1, padding: 10}}>
            <Text style={{fontSize: 20}}>Academic Manager</Text>
            <View style={{display: 'flex', flexDirection: 'row', justifyContent: 'space-around', paddingTop: 20, paddingBottom: 20}}>
                <TouchableOpacity style={{padding: 10, backgroundColor: '#EDEDEE'}} onPress={addSemester}>
                    <Text>Add Semester Button</Text>
                </TouchableOpacity>
                <TouchableOpacity style={{padding: 10, backgroundColor: '#EDEDEE'}} onPress={addStudyHours}>
                    <Text>Record Study Hours Button</Text>
                </TouchableOpacity>
            </View>
            <View style={{display: 'flex', flexDirection: 'row', justifyContent: 'space-around', paddingTop: 20, paddingBottom: 20}}>
                <Text>Courses Taken</Text>
                <Text>Applicable Courses</Text>
                <Text>GPA</Text>
            </View>
            <FlatList
                data={tempSemesterData}
                renderItem={({item, index}) =>
                    <View key={index} style={{display: 'flex', padding: 20, backgroundColor: '#EDEDEE'}}>
                        <Text>{item.semester}</Text>
                        <View style={{display: 'flex', flexDirection: 'row', justifyContent: 'space-around'}}>
                            <Text>Class Name</Text>
                            <Text>Credit Hours</Text>
                            <Text>Grade</Text>
                        </View>
                        {item.courses.map((course, index) => {
                            return (
                                <View key={index} style={{display: 'flex', flexDirection: 'row', justifyContent: 'space-around'}}>
                                    <Text>{course.name}</Text>
                                    <Text>{course.hours}</Text>
                                    <Text>{course.grade}</Text>
                                </View>
                            )
                        })}
                    </View>
                }
            />
        </View>
    );
}

export default AcademicManager;
