export const SET_CLASS_LIST = 'academics:setClassList';
export const APPEND_TO_CLASS_LIST = 'academics:appendToClassList';

export const setClassList = (classes) => {
    return {
        type: SET_CLASS_LIST,
        payload: classes
    }
}

export const appendToClassList = (newClasses) => {
    return {
        type: APPEND_TO_CLASS_LIST,
        payload: newClasses
    }
}
