import update from 'immutability-helper';

import {
    SET_CLASS_LIST,
    APPEND_TO_CLASS_LIST
} from '../actions/academics';

const initialState = {
    classes: []
}

export default function academicsReducer(state = initialState, action) {
    switch (action.type) {
        case SET_CLASS_LIST:
            return Object.assign({}, state, {
                classes: action.payload
            });
        case APPEND_TO_SELECTED_USER_FRIENDS:
            return update(state, {
                classes: {$push: action.payload}
            });
        default:
            return state;
    }
}
