    }, []);

    return (
        <View style={{display: 'flex', flex: 1, padding: 10}}>
            <View style={{display: 'flex', flexDirection: 'row', justifyContent: 'space-around', paddingTop: 20, paddingBottom: 20}}>
                <TouchableOpacity style={{padding: 10, backgroundColor: '#6995F5'}}>
                    <Text>Send New Message</Text>
                </TouchableOpacity>
                <View style={{display: 'flex', flexDirection: 'row', justifyContent: 'center', alignItems: 'center', padding: 20, backgroundColor: '#EDEDEE'}}>
                    <Text style={{padding: 10}}>Event Name</Text>
                    <TextInput onChangeText={setMessageText} style={{borderWidth: 1, borderColor: '#707070', height: 30}} />
                </View>
            </View>
            <FlatList
                data={props.messages}
                renderItem={({item, index}) => <Message navigation={props.navigation} item={item} index={index}/>}
            />
        </View>
    );
}


const mapStateToProps = ({ messageBoard }) => ({
    messages: messageBoard.messages
});

const mapDispatchToProps = (dispatch) => {
    return {
        setMessages: (messages) => dispatch(setMessageBoard(messages)),
        addNewMessage: (newEvent) => dispatch(addNewMessage(newEvent))

    }
}

export default connect(mapStateToProps, mapDispatchToProps)(MessageBoard);
